	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Print Alphabets in Reverse Order");
		
		  for(char p='Z'; p>='A'; p--) {
			  
			  System.out.print(" "+p);
			 System.out.println((byte)p);
		  }
	}

